import React from 'react';

const BenefitsSection = () => {
  const benefits = [
    {
      title: "Optimización para conversiones",
      description: "Creamos páginas enfocadas en guiar al usuario hacia la acción, maximizando resultados desde el primer clic."
    },
    {
      title: "Diseño profesional y adaptable",
      description: "Tu landing page se verá increíble en cualquier dispositivo, ofreciendo una experiencia consistente y de alto impacto."
    },
    {
      title: "Carga rápida y rendimiento eficiente",
      description: "Aplicamos buenas prácticas para tiempos de carga mínimos, evitando que pierdas clientes por demoras."
    }
  ];

  return (
    <section className="py-16 sm:py-20 bg-gradient-to-r from-blue-50 to-purple-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12 sm:mb-16">
          <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-gray-800 mb-4">
            ¿Por qué elegirnos?
          </h2>
          <p className="text-lg sm:text-xl text-gray-600 max-w-2xl mx-auto">
            Nuestro enfoque combina estrategia, diseño y tecnología para ofrecerte resultados reales.
          </p>
        </div>

        <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-3">
          {benefits.map((benefit, index) => (
            <div
              key={index}
              className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-all"
            >
              <h3 className="text-xl font-semibold text-blue-700 mb-2">{benefit.title}</h3>
              <p className="text-gray-700">{benefit.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default BenefitsSection;
